var FbSlider=new Class({Extends:FbElement,initialize:function(d,c){this.parent(d,c);this.plugin="slider";if(typeOf(this.options.value)==="null"){this.options.value=0}this.options.value=this.options.value.toInt();if(this.options.editable===true){if(typeOf(this.element)==="null"){fconsole("no element found for slider");return}var b=this.element.getElement(".fabrikinput");var e=this.element.getElement(".slider_output");this.mySlide=new Slider(this.element.getElement(".fabrikslider-line"),this.element.getElement(".knob"),{onChange:function(f){b.value=f;this.options.value=f;e.set("text",f);this.callChange()}.bind(this),steps:this.options.steps}).set(0);this.mySlide.set(this.options.value);b.value=this.options.value;e.set("text",this.options.value);var a=this.element.getElement(".clearslider");if(typeOf(a)!=="null"){a.addEvent("click",function(f){this.mySlide.set(0);b.value="";e.set("text","");f.stop()})}}},getValue:function(){return this.options.value},callChange:function(){typeOf(this.changejs)==="function"?this.changejs.delay(0):eval(this.changejs)},addNewEvent:function(a,b){if(a==="load"){this.loadEvents.push(b);this.runLoadEvent(b);return}if(a==="change"){this.changejs=b}}});